INSERT INTO `products` VALUES (1, 'Ấm trà TURKOIS', 224000.00, 'Ấm trà TURKOIS là lựa chọn hoàn hảo cho không gian chuyện trò ấm cúng. Thuộc bộ sưu tập thiết kế đặc biệt của BAYA được sản xuất tại Bát Tràng với nghệ thuật vẽ tay độc đáo, sản phẩm sở hữu kiểu dáng và gam màu xanh ngọc đẹp mắt. Họa tiết cá Koi bơi thành vòng tròn trong làn nước, lấy cảm hứng từ nền văn hóa Á Đông luôn tôn vinh vẻ đẹp của sự hài hòa, sung túc và viên mãn. Kết hợp với bộ tách trà cùng đĩa lót TURKOIS để mang phong cách truyền thống pha lẫn chút hiện đại cho ngôi nhà bạn thêm điểm nhấn.', 1, 100, 'storage/images/1.webp', 1);
INSERT INTO `products` VALUES (2, 'Bàn Ăn Gỗ Cao Su Màu Xám Vân Gỗ KENNY', 3990000.00, 'Tính năng nổi bật:\r\n\r\nSản phẩm được hoàn thiện với chất liệu gỗ MDF chất lượng với bề mặt ghế phủ lớp Veneer tần bì giúp bảo vệ lớp gỗ tốt, chống tình trạng mối mọt hay bị biến dạng bởi tác động nhiệt của thời tiết.\r\n\r\nMặt bàn và các chân ghế được hoàn thiện với chất lượng cao, mang lại tổng thể vừa đẹp mắt vừa có độ bền theo năm tháng. \r\n\r\n \r\nLợi ích:\r\n\r\nTính đa năng cao, có thể sử dụng trong nhiều mục đích khác nhau như: ăn uống, bàn trò chơi hay dùng để học tập, làm việc.\r\n\r\nBàn ăn giúp gắn kết mọi thành viên trong gia đình trong mọi bữa ăn.\r\n\r\nBàn ăn KENNY kích thước lớn rất phù hợp để tổ chức tiệc tối hoặc các sự kiện đặc biệt quan trọng.\r\n\r\nBàn ăn KENNY mang sắc xám đơn giản cùng thiết kế vân gỗ giúp tạo thêm điểm nhấn đặc biệt trong không gian bếp.\r\n\r\n \r\nKhông gian thích hợp:\r\n\r\nPhù hợp để đặt ở các phòng bếp-phòng ăn trong mọi gia đình. Tuyệt vời hơn, bạn có thể sử dụng bàn KENNY để làm nơi trang trí phòng khách như đặt tranh ảnh, hoa, nến, … hoặc thậm chí bạn có thể tận dụng để làm việc, làm bài tập', 1, 100, 'storage/images/2.webp', 1);
INSERT INTO `products` VALUES (3, 'Bàn Ăn MDF Chân Sắt VERONA', 995000.00, 'Bàn ăn VERONA nổi bật với màu trắng tinh khôi được làm từ gỗ công nghiệp bền chắc. Rất phù hợp với những gia đình ít người và không gian phòng bếp hạn chế. Mặt bàn được xử lý kỹ càng, mang vẻ đẹp tinh xảo. Chân bàn làm từ kim loại, cho độ cứng cáp và chắc chắn cao. Bạn có thể kết hợp bàn cùng các sản phẩm khác trong cùng bộ sưu tập VERONA để hoàn thiện nội thất phòng ăn của gia đình bạn.', 1, 100, 'storage/images/3.webp', 1);
INSERT INTO `products` VALUES (4, 'Bàn Cà Phê DOMINIK', 2790000.00, 'Bàn trà DOMINIK là một sản phẩm nội thất hiện đại, phù hợp với nhiều không gian phòng khách khác nhau. Bàn có kích thước 1000 x 550 x 450 mm, mang đến sự thoải mái và tiện nghi cho người sử dụng.\r\n\r\nBàn được làm từ gỗ cao cấp, có khả năng chống ẩm, chống mối mọt, mang đến độ bền cao. Thiết kế bàn đơn giản, tinh tế, với mặt bàn hình chữ nhật, phù hợp với nhiều phong cách nội thất khác nhau.\r\n\r\nBên cạnh đó, bàn trà DOMINIK còn có những ưu điểm nổi bật sau:\r\n\r\nKích thước đa dạng, phù hợp với nhiều không gian\r\nChất liệu cao cấp, bền bỉ\r\nThiết kế đơn giản, tinh tế\r\nGiá thành hợp lý.', 1, 100, 'storage/images/4.webp', 1);
INSERT INTO `products` VALUES (5, 'Bàn Cà Phê MARGARET', 2793000.00, 'Phòng khách nhà bạn không thể hoàn hảo nếu thiếu bàn cà phê MARGARET. Là sản phẩm đến từ nội thất BAYA, bàn được làm từ chất liệu gỗ cao su, MDF phủ veneer màu sáng sang trọng. Bạn có thiết kế đơn giản với 1 hộc kéo và 1 ngăn rỗng để bạn thoải mái chứa các vật dụng cần thiết. Kết hợp bàn cùng các sản phẩm khác trong bộ sưu tập MARGARET để hoàn thiện không gian nội thất.', 1, 100, 'storage/images/5.webp', 0);
INSERT INTO `products` VALUES (6, 'Bàn Cà Phê Ván Ép VIKING', 2490000.00, 'Bàn cà phê VIKING nằm trong đồ nội thất BAYA là sự lựa chọn lý tưởng và hoàn hảo cho không gian sống nhà bạn. Sản phẩm được làm hoàn toàn từ gỗ ván ép chắc chắn cùng độ nhẵn bóng đẹp mắt, tạo nên vẻ đẹp mộc mạc, giản đơn, nhưng không kém phần sang trọng, hiện đại. Đặc biệt, với thiết kế 2 tầng hình dạng bàn tròn tiện dụng, có thể để tạp chí, tách cà phê và các vật dụng khác gọn gàng, tiện lợi, giúp bạn tận hưởng cuộc sống thư thái và trọn vẹn hơn.', 1, 100, 'storage/images/6.webp', 0);
INSERT INTO `products` VALUES (7, 'Bàn Góc Di Động STUDENT', 499000.00, 'Bộ sưu tập nội thất văn phòng Student: Sự kết hợp hoàn hảo giữa Thẩm mỹ và Công năng sử dụng\r\n\r\n \r\n\r\nTrong nhịp sống hiện đại, nơi mà hoạt động làm việc và học tập tại nhà trở nên ngày càng quan trọng, việc chọn một sản phẩm bàn học hay tủ kệ văn phòng phù hợp là điều không thể thiếu. Những vật dụng này không chỉ phải đảm bảo tính chất lượng và chắc chắn mà còn phải thể hiện sự thẩm mỹ và phù hợp với môi trường làm việc và nội thất trong gia đình. Bộ sưu tập Student chính là sự lựa chọn hoàn hảo cho nhu cầu của bạn.\r\n\r\n \r\n\r\nKhung sắt và về mặt hoàn thiện xuất sắc\r\n\r\n \r\n\r\nKhung sắt là yếu tố quan trọng tạo nên sự chắc chắn và độ bền của các sản phẩm nội thất trong bộ sưu tập Student. Khung sắt được chế tạo bằng công nghệ tiên tiến, giúp đảm bảo tính cứng cáp và độ bền vượt trội. Với mức độ hoàn thiện cao, bạn có thể yên tâm sử dụng các sản phẩm trong thời gian dài mà không cần lo lắng về sự trầy xước hoặc biến dạng.\r\n\r\n \r\n\r\nMặt bàn và ngăn tủ kệ được làm từ gỗ công nghiệp cao cấp, chất lượng và hoàn thiện tinh tế. Gỗ công nghiệp không chỉ giúp bảo vệ môi trường mà còn đảm bảo tính chất lượng và đẹp mắt. Sự hoàn thiện tỉ mỉ trên mặt gỗ tạo nên một bề mặt mịn màng và dễ dàng vệ sinh, giúp bàn luôn giữ được vẻ đẹp ban đầu suốt thời gian dài.\r\n\r\n \r\n\r\nThiết kế hiện đại và thẩm mỹ\r\n\r\n \r\n\r\nBộ sản phẩm Student không chỉ có tính chất lượng và chắc chắn mà còn mang đậm phong cách hiện đại. Thiết kế đơn giản, gọn gàng và tiện dụng giúp các sản phẩm phù hợp với mọi không gian làm việc và gia đình. Bạn có thể dễ dàng kết hợp bàn, tủ kệ với các loại ghế và trang trí nội thất khác để tạo nên một môi trường làm việc thú vị và sáng tạo.', 1, 100, 'storage/images/7.webp', 0);
INSERT INTO `products` VALUES (8, 'Bàn Cà Phê Vân Gỗ Sồi CARINE', 2443000.00, 'Bạn đang tìm kiếm \"bạn đồng hành\" cho chiếc sofa của mình ư? Không cần phải tìm đâu xa nữa, chiếc bàn góc CARINE chắc hẳn sẽ là lựa chọn lý tưởng, đảm bảo phù hợp với hầu hết các không gian phòng!\r\n\r\nGồm 2 kệ với kích cỡ khác nhau, một dành cho ly thức uống ưu thích của bạn (hay bất kì gì bạn thích), và tầng còn lại cho sách, tạp chí, remote tv... bên cạnh thiết kế tròn trịa thanh lịch, không gì có thể sánh được chiếc bàn góc này đâu!\r\n\r\nSản phẩm là một thiết kế độc quyền của BAYA.', 1, 100, 'storage/images/8.webp', 0);
