INSERT INTO `products` VALUES (10, 'Bàn Ngoài Trời Thép Sơn Tĩnh Điện CAFE-ROYALE', 895000.00, 'Là thiết kế của BAYA, bàn ngoài trời CAFE ROYALE là lựa chọn hoàn hảo cho khu vườn nhà của bạn. Được làm bằng thép sơn tĩnh điện giúp hạn chế rỉ sét, bàn phù hợp để sử dụng ngoài trời. Bàn có thiết kế hiện đại với mặt bàn bo tròn và phần chân cứng cáp, góp phần làm đẹp thêm cho khu vườn. Hãy kết hợp sản phẩm trong cùng bộ sưu tập CAFE ROYALE để hoàn thiện không gian sống', 1, 100, 'storage/images/10.webp', 0);
INSERT INTO `products` VALUES (11, 'Bàn Trang Điểm Gỗ Cao Su Màu Xám Vân Gỗ KENNY', 2990000.00, 'Tính năng nổi bật:\r\n\r\nĐược hoàn thiện với chất liệu gỗ MDF với bề mặt ghế phủ lớp Veneer tần bì giúp bảo vệ lớp gỗ tốt, chống tình trạng mối mọt hay bị biến dạng bởi tác động nhiệt của thời tiết.\r\n\r\nThiết kế 1 ngăn kéo giúp bạn thuận tiện để cất giữ những vật dụng nhỏ trang điểm khác như: Son môi, phấn má, phấn mắt, … \r\n\r\nVới 4 chân gỗ được hoàn thiện cứng cáp giúp tổng thể sản phẩm trở nên thời thượng hơn\r\n\r\n \r\nLợi ích:\r\n\r\nGiữ cho góc trang điểm thêm phần ngăn nắp nhờ tích hợp 1 ngăn kéo, thuận tiện để người dùng cất giữ đồ trang điểm, trang sức hay một số phụ kiện khác. \r\n\r\nTăng tính thẩm mỹ cho căn hộ, bàn trang điểm KENNY có thể tạo thêm nét trang nhã và tinh tế cho phong cách trang trí phòng ngủ của bạn, nâng cao tính thẩm mỹ tổng thể của căn phòng.\r\n\r\nSử dụng đa chức năng, ngoài việc được sử dụng như một bàn trang điểm, bàn trang điểm còn có thể được sử dụng như một bàn làm việc hoặc không gian làm việc, mang đến một món đồ nội thất đa năng cho những không gian nhỏ.\r\n\r\n \r\nKhông gian thích hợp:\r\n\r\nBên cạnh việc thích hợp để đặt bàn trang điểm trong phòng ngủ thì sản phẩm cũng phù hợp để bạn đặt một góc nhỏ ở phòng khách. Ngoài ra, bạn cũng có thể dùng bàn trang điểm như một vật dụng để trang trí trong nhà như đặt nến, đặt hoa, … Hãy kết hợp với gương treo và ghế đôn KENNY để có một bộ bàn trang điểm hoàn chỉnh bạn nhé!', 1, 100, 'storage/images/11.webp', 0);
INSERT INTO `products` VALUES (12, 'Bàn trang điểm Gỗ thông Gỗ đậm CHESTER\r\n', 3143000.00, 'Góc làm đẹp của bạn xứng đáng được chăm chút tỉ mỉ khi ngày nào mình cũng dành thời gian nơi góc thân thuộc ấy!\r\n\r\nVới vẻ ngoài gọn gàng, thanh mảnh, bàn trang điểm CHESTER sẽ giúp tâm trạng mình luôn được thoải mái hơn bên cạnh thiết kế thông minh gồm nhiều ngăn kéo và hộc tủ giúp bạn cất giữ đồ mỹ phẩm và những vật dụng cần thiết.\r\n\r\nBàn trang điểm CHESTER với thiết kế nhỏ gọn cùng phần gương được gập mở dễ dàng. Sản phẩm vừa có thể làm bàn trang điểm lại vừa có thể trở thành bàn làm việc khi gấp gương xuống.', 1, 100, 'storage/images/12.webp', 0);
INSERT INTO `products` VALUES (13, 'Bàn Trang Điểm KITKA', 2443000.00, 'Bàn trang điểm KITKA là sản phẩm độc quyền đến từ nội thất BAYA với gam màu gỗ nâu đậm đẹp mắt. Sản phẩm được làm từ gỗ cao su cao cấp, phủ lớp veneer vừa tăng độ bền vừa khiến bàn thêm phần bóng đẹp. Bàn có 2 hộc kéo tiện dụng. Hãy kết hợp bàn với các sản phẩm giường hay tủ quần áo trong cùng bộ sưu tập để không gian căn hộ thêm sang trọng.', 1, 100, 'storage/images/13.webp', 0);
INSERT INTO `products` VALUES (14, 'Bàn Trang Điểm LUCAS', 3650000.00, 'Bàn trang điểm LUCAS là một sản phẩm nội thất tiện dụng, giúp bạn trang điểm và làm đẹp một cách dễ dàng. Bàn có kích thước 1200 x 540 x 760 mm, phù hợp với nhiều không gian phòng ngủ khác nhau.\r\n\r\nBàn được làm từ gỗ cao cấp, có khả năng chống ẩm, chống mối mọt, mang đến độ bền cao. Thiết kế bàn đơn giản, hiện đại, ngăn kéo tiện lợi . Bàn có thể kết hợp với ghế ngồi, giúp bạn thoải mái khi trang điểm và làm đẹp.\r\n\r\nDưới đây là một số ưu điểm nổi bật của bàn trang điểm LUCAS:\r\n\r\nKích thước phù hợp với nhiều không gian phòng ngủ\r\nChất liệu cao cấp, bền bỉ\r\nThiết kế đơn giản, hiện đại\r\nKệ đựng đồ, giúp bạn sắp xếp đồ dùng một cách gọn gàng', 1, 100, 'storage/images/14.webp', 0);
INSERT INTO `products` VALUES (15, 'Bao Ống Tay Chống Văng Dầu Mỡ 100% Polyester SARA', 20300.00, 'Ống bao tay chống văng dầu mỡ SARA (hay bao trùm khuỷu tay) được mệnh danh là cánh tay đắc lực của các bà nội trợ. Với thiết kế linh hoạt, bao tay giúp hạn chế tối đa dầu mỡ cũng như các vết bẩn dính lên tay áo khi sơ chế, nấu ăn… Sản phẩm nổi bật với:\r\n\r\nThiết kế: Chiều dài sản phẩm phù hợp, bao hết từ cổ tay đến khuỷu tay, giúp cho người dùng không lo bị ướt tay áo hay bị bẩn, bắn dầu mỡ vào tay.\r\nChất liệu: Polyester có độ trơn, bóng cao nên có khả năng chống thấm cao, bảo vệ tay áo, cổ tay… và dễ dàng vệ sinh.\r\nMàu sắc: Màu chủ đạo là màu trắng tinh khôi, nổi bật thêm với những bông hoa hồng nhỏ, tạo ra sự hài hòa nhẹ nhàng cho sản phẩm. \r\nĐiểm nổi bật: Thun co giãn ở hai đầu rất tiện lợi, giúp giữ bao tay không bị xê dịch, trơn tuột khi sử dụng.\r\nTính ứng dụng cao: Giúp chống bẩn, chống nước, ngăn dầu mỡ bắn vào cổ tay, tay áo, hạn chế tối đa gây trầy xước áo (đặc biệt là với áo len)... trong quá trình sơ chế, chế biến, nấu ăn, làm bánh…\r\nDễ dàng kết hợp với những sản phẩm nhà bếp khác như: găng tay bắc nồi, tạp dề, găng tay lò nướng…', 1, 100, 'storage/images/15.webp', 0);
INSERT INTO `products` VALUES (16, 'Bát Sứ Ăn Salad FLORAL Hoa Văn Hoa Lá\r\n', 89100.00, 'Với tông màu trắng, kèm họa tiết hoa lá, tạo nên cảm giác nhẹ nhàng, tinh tế, và một chút bay bổng. Bộ sưu tập gồm chén, bát, đĩa, bình, ly\r\nphù hợp với nhiều nhu cầu sử dụng. Sản phẩm được làm từ chất liệu sứ, đảm bảo an toàn sức khỏe cho người sử dụng.', 1, 100, 'storage/images/16.webp', 0);
INSERT INTO `products` VALUES (17, 'Bát Sứ Ăn Salad VICTORIA Hoa Văn Xanh\r\n', 80100.00, 'BAYA xin giới thiệu bộ sưu tập Victoria, cổ điển nhưng không nhàm chán, bộ sưu tập sẽ rất thích hợp cho những vị khách yêu thích sự sang trọng và tao nhã đem lại cho bạn cảm giác như một vị công nương kiều diễm hay một ngài bá tước uy quyền. Bộ sưu tập chú trọng nét tinh tế, nghệ thuật trong từng chi tiết với hoa văn tinh xảo, tỉ mỉ, thể hiện cá tính mang đậm dấu ấn và phong cách của chủ nhân. Sản phẩm được làm từ sứ đảm bảo an toàn sức khỏe, dễ dàng bảo quản, tiện lợi khi sử dụng, nâng tầm chất lượng sống của gia đình bạn.', 1, 100, 'storage/images/17.webp', 0);
INSERT INTO `products` VALUES (18, 'Bếp Nướng Thép Không Gỉ Xanh Lá Cây TEXAS\r\n', 349500.00, 'Lò nướng TEXAS là sản phẩm lò nướng BBQ phù hợp đối với những bữa tiệc ngoài nhà bạn.\r\n\r\n- Thiết kế lò nướng nhỏ gọn, thông minh, không tốn nhiều diện tích, dễ dàng mang vác cho những buổi tiệc ngoài trời, picnic...\r\n\r\n- Lò nướng có thể giữ được độ tươi ngon của thực phẩm mà không bị ám mùi khói, giữ được vị béo ngậy của thực phẩm.\r\n\r\n-  Lò nướng có độ bền cao, bền bỉ theo thời gian, giúp cho bạn luôn có được những trải nghiệm nấu nướng tuyệt vời.', 1, 100, 'storage/images/18.webp', 0);
