INSERT INTO `products` VALUES (28, 'Kệ Trưng Bày 3 Tầng Gỗ Sồi LIPIZZAN', 999000.00, 'Được làm từ chất liệu MDF phủ veneer sồi bền chắc, giá để đồ LIPIZZAN nổi bật với thiết kế đơn giản nhưng sang trọng và cực kỳ tiện dụng. Sản phẩm giúp bạn sắp xếp và bày trí các vật dụng vừa ngăn nắp vừa nghệ thuật, đẹp mắt. Giá để đồ có nhiều màu sắc và kích thước khác nhau cho bạn thoải mái chọn lựa. Đây là dòng sản phẩm cực kỳ độc đáo đến từ nội thất BAYA.', 2, 1, 'storage/images/28.webp', 0);
