INSERT INTO `products` VALUES (30, 'Kệ trưng bày MFC Xám ICONICO', 1743000.00, 'Sản phẩm được thiết kế theo phong cách hiện đại, có kích thước 75 x 35 x 75 cm, được chia thành 4 ngăn, với sức chứa tối đa là 30 kg. Sản phẩm có sự đa dạng trong màu sắc, có tới 5 màu: xám đậm, xanh oliu, vân gỗ sồi, san hô, trắng để khách hàng thoải mái lựa chọn.\r\nKhông những vậy, Kệ ICONICO còn có khả năng kết hợp với các sản phẩm ICONICO khác  và 4 nút bịt chân bàn (có thể tự điều chỉnh), có thể điều chỉnh để phù hợp với yêu cầu của khách hàng.\r\nVề tổng thể, có thể thấy sản phẩm được thiết kế vô cùng thông minh, linh hoạt và đa năng. Không chỉ đảm bảo về mặt công năng, giúp gia chủ tối ưu không gian sống, mà còn thỏa mãn tính thẩm mỹ, mang lại vẻ đẹp sang trọng cho ngôi nhà.\r\nSản phẩm được sản xuất từ nguồn gỗ đạt tiêu chuẩn của FSC về Quản lý và khai thác gỗ bền vững, đồng thời đạt chứng nhận an toàn cho sức khỏe người dùng theo tiêu chuẩn Châu Âu.\r\nNhờ vào chất liệu đạt tiêu chuẩn, sản phẩm cũng giúp chủ nhân thuận lợi hơn trong việc vệ sinh, tiết kiệm được nhiều thời gian cho công việc dọn dẹp.', 2, 1, 'storage/images/30.webp', 0);
INSERT INTO `products` VALUES (31, 'Kệ Trưng Bày Zig Zag KERMIT', 50000.00, 'Kệ trưng bày KERMIT với thiết kế đặc biệt cho bạn thoải mái sắp xếp và trang trí không gian sống của mình một cách sáng tạo. Bạn có thể gắn kệ vào tường theo hình bậc thang hoặc gợn sóng tùy thích. Kệ chắc chắn, với bề mặt trơn nhẵn, không thấm nước và dễ lau chùi; là nơi lý tưởng để bạn cất gọn sách, chưng chậu cây hay những vật nhỏ xinh tô điểm cho ngôi nhà.', 2, 1, 'storage/images/31.webp', 0);
INSERT INTO `products` VALUES (32, 'Khung chính bàn, tủ MFC/ Kim loại Nâu/ Đen VAGABOND', 507000.00, 'Khung nối tiếp trong bộ nội thất lắp ghép VAGABOND được thiết kế theo phong cách châu Âu đương đại đơn giản, chắc chắn và dễ dàng tháo lắp. Bạn có thể mua riêng các chi tiết để thỏa sức sáng tạo mẫu thiết kế yêu thích của mình, hoặc chọn từ 5 mẫu bàn/ tủ/ kệ lắp ghép sẵn phù hợp cho văn phòng tại gia và phòng ngủ có không gian nhỏ.#', 2, 1, 'storage/images/32.webp', 0);
INSERT INTO `products` VALUES (33, 'Nệm Có Chân 90 FUKUOKA', 3990000.00, 'Kích thước:\r\n\r\nChiều dài: 190 cm\r\n\r\nChiều rộng: 90 cm\r\n\r\nĐộ dày: 34 cm\r\n\r\nNệm có chân Fukuoka: Sự Kết hợp Hoàn hảo cho sự Tiện lợi và Sáng tạo\r\n\r\nĐối với những hộ gia đình có con nhỏ hoặc thường xuyên đón tiếp khách, việc có một sản phẩm đa năng và thú vị như nệm foam có chân Fukuoka là một lựa chọn tuyệt vời. Sản phẩm này không chỉ là một chiếc ghế ngồi thoải mái mà còn có thể được sử dụng để tạo ra không gian phòng gọn gàng hoặc chỗ ngủ dự phòng cho bạn và khách của bạn. Dưới đây là một số điểm nổi bật về sản phẩm này:\r\n\r\nThiết kế Hiện đại và Gọn gàng:\r\n\r\nNệm Fukuoka có thiết kế nhỏ gọn và hiện đại có thể phù hợp với rất nhiều không gian phòng. Từ kết hợp với các màu sắc tươi sáng cho phòng trẻ em đến các tông màu đơn giản cho phòng dành cho khách, sản phẩm luôn thích ứng hoàn hảo.\r\n\r\nChất liệu Foam Cao cấp:\r\n\r\nPhần quan trọng nhất của sản phẩm chính là lớp nệm foam bên trong với độ đàn hồi và độ thoáng khí cao, mang lại sự thoải mái và êm ái tuyệt vời cho người sử dụng. Fukuoka thích hợp để ngồi, nằm hay thậm chí là để thư giãn một cách thoải mái.\r\n\r\nĐa Dụng và Linh Hoạt:\r\n\r\nNệm Fukuoka có thể sử dụng cho nhiều mục đích khác nhau. Bạn có thể sử dụng nó làm giường cho trẻ em, giường cho khách, hoặc thậm chí là ghế để ngồi chơi nếu cần. Chân sắt có thể tháo rời cùng trọng lượng nhẹ khiến việc lắp đặt hoặc cất trữ trở nên hết sức dễ dàng, giúp bạn tiết kiệm được thêm không gian.', 2, 1, 'storage/images/33.webp', 0);
INSERT INTO `products` VALUES (34, 'Nệm Đơn 90, lò xo gấp gọn SAPA', 2490000.00, 'Nệm Sapa kích thước: Dài 190cm, rộng 90cm và dày 17cm, phù hợp với giường đơn Sapa.\r\n\r\nChất liệu: Foam, lò xo. Đóng họp nhỏ gọn, vỏ hộp có gắn bánh xe và tay kéo, rất tiện dụng khi vận chuyển lên thang máy chung cư, dốc đứng, ngõ nhà nhỏ hẹp.', 2, 1, 'storage/images/34.webp', 0);
INSERT INTO `products` VALUES (35, 'Nệm Foam 1m2, 1m6, 1m8 TONKIN', 6490000.00, 'Nệm Foam Tonkin - Giấc Ngủ Trọn Vẹn\r\n\r\nKích thước:\r\n\r\nChiều dài: 200 cm\r\n\r\nChiều rộng: 120 cm\r\n\r\nĐộ dày: 23 cm\r\n\r\nGiấc ngủ là một phần quan trọng trong cuộc sống của chúng ta, và để có một giấc ngủ trọn vẹn, một chiếc nệm chất lượng là điều không thể thiếu. Sản phẩm Tonkin là sản phẩm mới nhất trong Bộ sưu tập Mùa đông của Baya.\r\n\r\nChất liệu foam cao cấp:\r\n\r\nNệm Foam Tonkin được sản xuất bằng chất liệu foam cao cấp, đặc biệt là foam bọt khí tự nhiên, giúp nệm đảm bảo sự thoải mái và độ bền vượt trội. Foam này được tạo ra từ sự kết hợp của nhiều lớp foam khác nhau, tạo ra một bề mặt mềm mại và đàn hồi, giúp người sử dụng có cảm giác êm ái và thoải mái khi nằm trên nệm.\r\n\r\nHệ thống hỗ trợ cơ thể hoàn hảo:\r\n\r\nNệm Foam Tonkin được thiết kế với một hệ thống hỗ trợ cơ thể độc đáo. Các vùng cứng và mềm trải dọc theo nệm giúp tạo ra sự phân bố áp lực đồng đều trên toàn bộ cơ thể, giúp giảm căng thẳng và đau đớn trong khi nằm. Điều này không chỉ giúp bạn có giấc ngủ ngon hơn mà còn tạo ra lợi ích cho sức khỏe cơ xương và cột sống của bạn.\r\n\r\nKhả năng chống nấm mốc và khuẩn:\r\n\r\nMột điểm mạnh của nệm Foam Tonkin là khả năng chống nấm mốc và khuẩn. Vật liệu foam chống nấm mốc tự nhiên và không thấm nước, giúp ngăn chặn sự phát triển của vi khuẩn và nấm mốc trong nệm. Điều này giúp duy trì môi trường ngủ sạch sẽ và an toàn cho sức khỏe của bạn.\r\n\r\nThoải mái và êm ái:\r\n\r\nNệm Foam Tonkin được thiết kế để đảm bảo sự thoải mái tối đa. Không chỉ có khả năng tạo ra bề mặt mềm mại và êm ái, mà còn có khả năng thấm hút và thoát ẩm tốt, giúp bạn luôn có cảm giác mát mẻ và khô ráo khi nằm trên nệm.\r\n\r\nĐa dạng về kích thước:\r\n\r\nNệm Foam Tonkin có sẵn trong nhiều kích thước khác nhau, từ đơn, đôi, đến king size, phù hợp với nhu cầu và không gian ngủ của mọi người.', 2, 1, 'storage/images/35.webp', 0);
INSERT INTO `products` VALUES (36, 'Ruột Gối Ngủ Sợi Kapok NSLEEP', 599000.00, 'Nsleep là một sản phẩm ruột gối cao cấp, được thiết kế để mang lại sự thoải mái và thư giãn cho người dùng. Nsleep có những đặc điểm nổi bật sau:\r\n\r\n \r\nVỏ gối được làm từ vải tencel, một loại vải tự nhiên có khả năng thấm hút ẩm và kháng khuẩn tốt. Vải tencel còn có độ mềm mại và mịn màng, không gây kích ứng da hay tạo nếp nhăn.\r\n\r\n \r\nRuột gối được làm từ bông kapok, một loại bông thực vật có nguồn gốc từ cây gòn. Bông kapok có độ bền cao, không bị xẹp lún hay biến dạng theo thời gian. Bông kapok cũng có tính năng chống ẩm, chống nấm mốc và chống côn trùng.\r\n\r\n \r\nKích thước của ruột gối là 70cm x 50cm, phù hợp với nhiều loại đệm và ga trải giường. Trọng lượng của ruột gối là 850g, vừa phải để tạo cảm giác êm ái và vừa vặn.\r\n\r\n \r\nSản phẩm được sản xuất tại Việt Nam, đảm bảo chất lượng và an toàn cho sức khỏe.\r\n\r\n \r\nNsleep là sự lựa chọn hoàn hảo cho những ai muốn có một giấc ngủ ngon và sâu. Nsleep sẽ giúp bạn giảm căng thẳng, thư giãn cơ thể và tinh thần. Hãy đến với Nsleep để trải nghiệm sự khác biệt!', 2, 1, 'storage/images/36.webp', 0);
INSERT INTO `products` VALUES (37, 'Ruột Gối Ngủ Sợi Kapok NSLEEP Cho Trẻ Em', 299000.00, 'Ruột gối ngủ cho trẻ em Nsleep\r\n\r\nRuột gối ngủ cho trẻ em Nsleep là một sản phẩm chất lượng cao, được sản xuất tại Việt Nam với chất liệu vỏ bằng vải tencel và ruột bằng bông kapok. Vải tencel là một loại vải được làm từ cellulose, có độ bền cao, thoáng khí và thân thiện với môi trường. Bông kapok là một loại bông tự nhiên có độ mềm mại và đàn hồi cao, giúp trẻ ngủ ngon và sâu giấc hơn.\r\n\r\nRuột gối ngủ cho trẻ em Nsleep có kích thước chiều dài 40cm, rộng 45cm và trọng lượng 220g. Ruột gối có độ cao vừa phải, phù hợp với mọi tư thế nằm của trẻ em. Ruột gối được thiết kế với lỗ thông hơi ở mặt dưới, giúp thoáng khí và không bị bí bách khi sử dụng.\r\n\r\nRuột gối ngủ cho trẻ em Nsleep là một sản phẩm lý tưởng cho những trẻ có nhu cầu ngủ ngon và sâu giấc. Ruột gối có thể sử dụng cho mọi lứa tuổi, từ trẻ sơ sinh đến trẻ lớn. Ruột gối cũng có thể được sử dụng cho những trẻ có vấn đề về hô hấp, như hen suyễn hoặc viêm phế quản.\r\n\r\nRuột gối ngủ cho trẻ em Nsleep là một sản phẩm chất lượng cao, có giá cả hợp lý. Nếu bạn đang tìm kiếm một chiếc ruột gối ngủ cho trẻ em chất lượng cao, hãy đến với Nsleep.\r\n\r\nƯu điểm của ruột gối ngủ cho trẻ em Nsleep\r\n\r\nChất liệu vỏ bằng vải tencel cao cấp, thoáng khí và thân thiện với môi trường.\r\nRuột gối bằng bông kapok tự nhiên, mềm mại và đàn hồi cao.\r\nKích thước vừa phải, phù hợp với mọi tư thế nằm của trẻ em.\r\nThiết kế với lỗ thông hơi ở mặt dưới, giúp thoáng khí và không bị bí bách khi sử dụng.', 2, 1, 'storage/images/37.webp', 0);
INSERT INTO `products` VALUES (38, 'Ruột Gối Ngủ Sợi Kapok NSLEEP Cho Trẻ Sơ Sinh', 199000.00, 'Ruột Gối SIMPLE được làm từ chất liệu thông thoáng, êm ái và có độ bền cao, mang đến cho bạn giấc ngủ thật ngon. Hãy sử dụng kèm vỏ gối để tăng thêm vẻ đẹp cho phòng ngủ đồng thời vệ sinh gối dễ dàng hơn.', 2, 1, 'storage/images/38.webp', 0);
INSERT INTO `products` VALUES (39, 'Ruột Gối Ngủ Vải Cotton SIMPLE', 139300.00, 'Gối ôm Nsleep\r\n\r\nGối ôm Nsleep là một sản phẩm chất lượng cao, được sản xuất tại Việt Nam với chất liệu vỏ bằng vải tencel và ruột bằng bông kapok. Vải tencel là một loại vải được làm từ cellulose, có độ bền cao, thoáng khí và thân thiện với môi trường. Bông kapok là một loại bông tự nhiên có độ mềm mại và đàn hồi cao, giúp bạn ngủ ngon và sâu giấc hơn.\r\n\r\nGối ôm Nsleep có kích thước chiều dài 105cm, đường kính 20cm và trọng lượng 1100g. Gối có độ cao vừa phải, phù hợp với mọi tư thế nằm. Gối được thiết kế với lỗ thông hơi ở mặt dưới, giúp thoáng khí và không bị bí bách khi sử dụng.\r\n\r\nGối ôm Nsleep là một sản phẩm lý tưởng cho những người có nhu cầu ngủ ngon và sâu giấc. Gối có thể sử dụng cho mọi lứa tuổi, từ trẻ em đến người lớn. Gối cũng có thể được sử dụng cho những người có vấn đề về hô hấp, như hen suyễn hoặc viêm phế quản.\r\n\r\nGối ôm Nsleep là một sản phẩm chất lượng cao, có giá cả hợp lý. Nếu bạn đang tìm kiếm một chiếc gối ôm chất lượng cao, hãy đến với Nsleep.\r\n\r\nƯu điểm của gối ôm Nsleep\r\n\r\nChất liệu vỏ bằng vải tencel cao cấp, thoáng khí và thân thiện với môi trường.\r\nRuột gối bằng bông kapok tự nhiên, mềm mại và đàn hồi cao.\r\nKích thước vừa phải, phù hợp với mọi tư thế nằm.\r\nThiết kế với lỗ thông hơi ở mặt dưới, giúp thoáng khí và không bị bí bách khi sử dụng.', 2, 1, 'storage/images/39.webp', 0);
