INSERT INTO `products` VALUES (41, 'Bộ Hũ Gia Vị Sứ Trắng/ Xanh Da Trời REGATTA', 100000.00, 'Bộ hũ gia vị REGATTA từ BAYA nổi bật với viền men màu trên nền chất liệu sứ trắng. Sản phẩm có kiểu dáng pha lẫn truyền thống và hiện đại, góp phần làm đẹp bàn ăn gia đình. Kết hợp cùng các phụ kiện đẹp mắt khác trong bộ sưu tập REGATTA để hoàn thiện việc bày trí cho không gian dùng bữa.', 3, 1, 'storage/images/41.webp', 0);
INSERT INTO `products` VALUES (42, 'Ca Đo Lường Nhựa Trong Suốt ∙ Thể Tích 600ml RECIPE', 48300.00, 'Cốc đo lường RECIPE dung tích 500ml thuộc bộ sưu tập dùng trong nhà bếp mới của nội thất BAYA với chất liệu an toàn cùng thiết kế hiện đại và tiện dụng. Đây là sản phẩm giúp việc định lượng pha chế và chiết rót trong quá trình nấu nướng trở nên dễ dàng hơn bao giờ hết nhờ vạch chỉ ml chính xác. Sản phẩm được làm bằng nhựa cao cấp trong suốt với độ bền cao cho thời gian sử dụng lâu dài. Kết hợp trọn bộ sản phẩm RECIPE cho căn bếp hoàn chỉnh của gia đình bạn.', 3, 1, 'storage/images/42.webp', 0);
INSERT INTO `products` VALUES (43, 'Cặp Hũ Rắc Muối Và Tiêu Nhựa RECIPE', 69300.00, 'Bộ hũ muối & tiêu RECIPE thuộc bộ sưu tập dùng trong nhà bếp mới của nội thất BAYA với chất liệu cao cấp, an toàn cùng thiết kế hiện đại và tiện dụng. Sản phẩm được làm từ chất liệu nhựa cứng trong suốt, phối màu trang nhã và sang trọng tạo điểm nhấn trên bàn ăn. 2 hũ có nam châm giúp cố định bộ đôi với nhau một cách dễ dàng, tiện lợi khi sử dụng. Kết hợp trọn bộ sản phẩm RECIPE cho căn bếp hoàn chỉnh của gia đình bạn.', 3, 1, 'storage/images/43.webp', 0);
INSERT INTO `products` VALUES (44, 'Cây Vét Bột Silicone/ Gỗ Màu Xanh Ngọc RECIPE', 69300.00, 'Dụng cụ vét bột RECIPE thuộc bộ sưu tập dùng trong nhà bếp mới của nội thất BAYA với chất liệu an toàn cùng thiết kế hiện đại và tiện dụng. Đây là sản phẩm giúp việc trộn/ vét bột trong quá trình làm bánh trở nên dễ dàng hơn bao giờ hết nhờ kết cấu mỏng nhẹ, với tay cầm bằng gỗ bọc nhựa chắc chắn. Sản phẩm được làm bằng 100% silicon cao cấp với độ bền cao cho thời gian sử dụng lâu dài. Kết hợp trọn bộ sản phẩm RECIPE cho căn bếp hoàn chỉnh của gia đình bạn.', 3, 1, 'storage/images/44.webp', 0);
INSERT INTO `products` VALUES (45, 'Chén Đựng Snack Gỗ Cao Su NAPA', 249000.00, 'Chén đựng snack NAPA thuộc bộ sưu tập vật dụng bàn ăn cùng tên của BAYA lấy cảm hứng từ những bữa tiệc nướng BBQ theo phong cách Âu Mỹ. Sản phẩm làm từ gỗ cao su bền chắc, an toàn cho sức khoẻ với lớp sơn thủ công màu giả cổ độc đáo phủ PU bóng đẹp. Chén đựng snack NAPA phù hợp với các món salad hoặc hạt khô, ô-liu… Bày trí bàn ăn thật bắt mắt với bộ sưu tập NAPA đẹp mộc mạc, thân thiện với môi trường cho không gian dùng bữa của bạn một phong cách riêng thật đặc biệt!', 3, 1, 'storage/images/45.webp', 0);
