INSERT INTO `products` VALUES (59, 'Đĩa Sứ VICTORIA Hoa Văn Xanh', 89100.00, 'BAYA xin giới thiệu bộ sưu tập Victoria, cổ điển nhưng không nhàm chán, bộ sưu tập sẽ rất thích hợp cho những vị khách yêu thích sự sang trọng và tao nhã đem lại cho bạn cảm giác như một vị công nương kiều diễm hay một ngài bá tước uy quyền. Bộ sưu tập chú trọng nét tinh tế, nghệ thuật trong từng chi tiết với hoa văn tinh xảo, tỉ mỉ, thể hiện cá tính mang đậm dấu ấn và phong cách của chủ nhân. Sản phẩm được làm từ sứ đảm bảo an toàn sức khỏe, dễ dàng bảo quản, tiện lợi khi sử dụng, nâng tầm chất lượng sống của gia đình bạn.', 3, 1, 'storage/images/59.webp', 0);
INSERT INTO `products` VALUES (60, 'Đĩa TURKOIS', 55300.00, 'Đĩa TURKOIS thuộc bộ sưu tập thiết kế đặc biệt của BAYA được sản xuất tại Bát Tràng với nghệ thuật vẽ tay độc đáo. Sản phẩm sở hữu kiểu dáng đẹp mắt và gam màu xanh ngọc mang hơi thở thiên nhiên vào gian bếp và phòng ăn của gia đình bạn. Họa tiết cá Koi bơi thành vòng tròn trong làn nước, lấy cảm hứng từ nền văn hóa Á Đông luôn tôn vinh vẻ đẹp của sự hài hòa, sung túc và viên mãn. Chọn TURKOIS để mang phong cách truyền thống đan xen chút hiện đại vào không gian dùng bữa cho ngôi nhà bạn thêm điểm nhấn.', 3, 1, 'storage/images/60.webp', 0);
INSERT INTO `products` VALUES (61, 'Ghế văn phòng xoay nâng hạ JOY', 990000.00, 'Ghế văn phòng Joy với bộ khung chắc chắn, nệm foam và bọc vải mesh là một lựa chọn phù hợp cho bàn học/bàn làm việc gia đình hoặc không gian công sở nhỏ.\r\n\r\n1. Thiết kế:\r\n\r\n- Ghế Joy có thiết kế trẻ trung và hiện đại, thích hợp cho các môi trường văn phòng hiện đại, như văn phòng công ty, cơ quan, hoặc không gian làm việc tại nhà.\r\n\r\n- Ghế có kích thước nhỏ gọn, phù hợp với không gian nhỏ hoặc tạo ra sự gọn gàng cho văn phòng của bạn.\r\n\r\n2. Đệm foam:\r\n\r\n- Ghế Joy được trang bị đệm foam bọc bên trong lớp vải mesh, giúp cung cấp sự thoải mái và hỗ trợ cho người sử dụng trong thời gian dài.\r\n\r\n- Foam có độ đàn hồi tốt, giúp giảm áp lực lên cơ lưng và cột sống, làm giảm mệt mỏi trong quá trình làm việc hoặc làm công việc liên quan đến ngồi nhiều giờ.\r\n\r\n3. Vải mesh:\r\n\r\n- Lớp vải mesh trải dọc lưng ghế Joy giúp cải thiện thông thoáng và thoáng khí, giúp người sử dụng cảm thấy thoải mái và không bị nóng khi ngồi trong thời gian dài.\r\n\r\n- Vải mesh thường làm từ các sợi vải có độ co dãn, tạo sự ôm vừa vặn và hỗ trợ cho lưng và cổ.', 4, 1, 'storage/images/61.webp', 0);
INSERT INTO `products` VALUES (62, 'Ghế Văn Phòng / Ghế Giám Đốc Bọc Da ETHAN', 3990000.00, 'Ghế văn phòng ETHAN là một sản phẩm ghế văn phòng cao cấp với thiết kế hiện đại, đẹp mắt và rất thoải mái. Ghế được làm từ vật liệu chất lượng cao như da tổng hợp hoặc vải bố, kết hợp với khung thép bền chắc, đem lại cho người sử dụng một sự thoải mái tối đa.\r\n\r\nGhế ETHAN được thiết kế với nhiều tính năng thông minh, như điều chỉnh chiều cao và độ nghiêng của lưng ghế, giúp người sử dụng có thể tùy chỉnh để phù hợp với cơ thể và tạo cảm giác thoải mái khi làm việc. Đặc biệt, ghế còn có bộ đệm êm ái và cần hơi gió bằng tay, giúp người sử dụng giảm bớt mệt mỏi và căng thẳng trong quá trình làm việc.\r\n\r\nVới sự kết hợp giữa thiết kế hiện đại và tính năng thông minh, ghế văn phòng ETHAN là một lựa chọn tuyệt vời cho những ai muốn tìm kiếm một sản phẩm ghế văn phòng chất lượng cao, thoải mái và phù hợp với phong cách trang trí hiện đại của văn phòng.', 4, 1, 'storage/images/62.webp', 0);
