INSERT INTO `products` VALUES (67, 'Kệ Gỗ – Kệ Sách MOHO OSLO 901', 174900.00, 'Bộ sưu tập nội thất OSLO lấy ý tưởng từ những nhịp sống hiện đại, hối hả của thành phố OSLO của Na Uy. Đặc trưng của các sản phẩm từ bộ sưu tập nội thất này là những đường nét bo tròn đến hoàn hảo. Đặc biệt chi tiết hình tròn được tận dụng một cách độc đáo và mới mẻ vào trong từng thiết kế, thổi làn gió hiện đại cho mọi không gian. \r\nKhông gian lưu trữ lớn\r\nThiết kế ngăn kệ rộng, chịu tải trọng lớn giúp tối ưu khả năng lưu trữ.\r\nĐộ hoàn thiện cao \r\nChi tiết vân gỗ tự nhiên, tỉ mỉ trong từng đường nét mang đến không gian nội thất hiện đại. \r\nCó 02 màu chọn lựa\r\nTủ kệ sách OSLO hiện có 02 màu gồm: màu tự nhiên và nâu. Khách hàng chọn lựa màu sắc phù hợp với những món đồ nội thất khác MOHO để nên không gian đồng điệu về phong cách', 4, 1, 'storage/images/67.webp', 0);
INSERT INTO `products` VALUES (68, 'Bàn Làm Việc Gỗ MOHO VLINE 601 Màu Nâu', 1590000.00, 'Gỗ công nghiệp \r\n\r\nBàn làm việc sử dụng chất liệu gỗ công nghiệp (PB, MDF) đạt chuẩn CARB-P2 an toàn tuyệt đối cho người sức khỏe người dùng và đạt chứng nhận FSC bảo vệ và phát triển rừng, góp phần vào sự phát triển rừng một cách bền vững.\r\n\r\nGỗ tràm\r\n\r\nChất liệu gỗ tràm tự nhiên đảm bảo vệ độ chắc chắn cao, chống công vênh, mối mọt cho bàn làm việc.', 4, 1, 'storage/images/68.webp', 0);
INSERT INTO `products` VALUES (69, 'Bàn Làm Việc Gỗ MOHO VLINE 601 Màu Tự Nhiên', 1890000.00, 'Các thiết kế trong bộ sưu tập nội thất VLINE mang trong mình nét đẹp đặc trưng của hồn Việt cùng vẻ đẹp năng động của thời đại. V” là viết tắt của từ “Việt” trong “Việt Nam”. “LINE” là các đường nét mang xu hướng hiện đại, phóng khoáng của ngày nay. \r\n\r\nBàn có kích thước vừa phải\r\nVới kích thước vừa phải, gọn gàng, không chiếm quá nhiều diện tích, độ rộng vừa đủ có thể để được nhiều vật dụng như: tài liệu, máy vi tính, laptop, sách.', 4, 1, 'storage/images/69.webp', 0);
INSERT INTO `products` VALUES (70, 'Bàn Làm Việc Gỗ MOHO WORKS 701', 1990000.00, 'Mặt bàn rộng rãi, thoải mái\r\nBàn làm việc gỗ MOHO WORKS 701 sở hữu kích thước dài 120cm x rộng 60cm đảm bảo sự thoải mái cho người dùng bày biện nhiều đồ vật từ máy tính bàn hoặc laptop, sách vở, dụng cụ làm việc khác mà vẫn giữ được không gian làm việc tiện lợi.', 4, 1, 'storage/images/70.webp', 0);
INSERT INTO `products` VALUES (71, 'Hộc Tủ 3 Ngăn Lưu Trữ Tài Liệu Có Khóa MOHO WORKS 201', 1190000.00, 'Thiết kế nhỏ gọn\r\nVới chiều rộng chỉ 35cm, hộc tủ dễ dàng đặt bên dưới bàn làm việc mà không gây cản trở không gian, tạo sự thuận tiện và thoải mái cho người dùng.\r\n3 tầng lưu trữ\r\nMặc dù tủ có thiết kế nhỏ gọn nhưng vẫn được thiết kế có 3 tầng để sắp xếp, phân loại và lưu trữ hồ sơ. Vừa vặn với giấy tờ có kích thước A4.\r\nKhóa tủ an toàn\r\nNgăn đầu tiên được trang bị khóa kim loại an toàn và cao cấp để bạn cất giữ các vật dụng cá nhân hoặc hồ sơ quan trọng.\r\nRay trượt IVANs\r\nNgăn tủ sử dụng ray trượt IVANs bền bỉ chống rỉ sét, mang đến trải nghiệm êm ái khi đóng mở ngăn tủ.\r\nTrang bị bánh xe kèm khóa chốt\r\nHộc tủ được trang bị bánh xe nhựa cao cấp ở 4 góc, quay 360 độ, đi kèm khóa chốt giúp dễ dàng di chuyển cũng như cố định tại bất kì vị trí nào.', 4, 1, 'storage/images/71.webp', 0);
INSERT INTO `products` VALUES (72, 'Kệ Gỗ – Kệ Sách MOHO OSLO 902', 1959000.00, 'Bộ sưu tập nội thất OSLO lấy ý tưởng từ những nhịp sống hiện đại, hối hả của thành phố OSLO của Na Uy. Đặc trưng của các sản phẩm từ bộ sưu tập nội thất này là những đường nét bo tròn đến hoàn hảo. Đặc biệt chi tiết hình tròn được tận dụng một cách độc đáo và mới mẻ vào trong từng thiết kế, thổi làn gió hiện đại cho không gian nội thất phòng khách.\r\n\r\nThiết kế tối ưu thông minh\r\n\r\nCó nhiều ngăn kệ để đồ tiện dụng, trang bị thêm cửa đóng mở để lưu trữ các đồ vật quan trọng, tránh được bụi bẩn.', 4, 1, 'storage/images/72.webp', 0);
INSERT INTO `products` VALUES (73, 'Ghế Xoay Văn Phòng Tay Gập Thông Minh MOHO RIGA 701', 1390000.00, 'Nhựa cao cấp\r\nKhung ghế, tựa tay và bánh xe được làm bằng nhựa cao cấp có độ bền sử dụng cao\r\n\r\nVải lưới mềm mại thoáng khí\r\nVới chất liệu vải nhập cao cấp, thoáng khí giúp bạn luôn thoải mái dù sử dụng trong nhiều giờ', 4, 1, 'storage/images/73.webp', 0);
INSERT INTO `products` VALUES (74, 'Ghế Văn Phòng Chân Xoay MOHO MAJOR 701', 1329000.00, 'Thiết kế bo cong mềm mại và bền bỉ\r\nGhế Văn Phòng Chân Xoay MOHO MAJOR 701 sử dụng khung nhựa bo cong từng đường nét vừa tạo cảm giác mềm mại, thiết kế hiện đại linh hoạt vừa hỗ trợ lưng và cột sống mang lại cảm giác thoải mái, hạn chế nhức mỏi khi sử dụng lâu.', 4, 1, 'storage/images/74.webp', 0);
INSERT INTO `products` VALUES (75, 'Bàn Máy Tính Gỗ MOHO WORKS 702', 2790000.00, 'Bàn máy tính gỗ MOHO WORKS 702 sở hữu kích thước của mẫu bàn làm việc tiêu chuẩn với Dài 120cm x Rộng 60cm đảm bảo sự thoải mái cho người dùng, bạn dễ dàng bài trí tối đa 3 màn hình máy tính đi kèm với đó các dụng cụ, thiết bị công nghệ khác. Ngoài ra, bàn máy tính gỗ MOHO WORKS 702 được thiết kế thêm 01 giá đỡ ổ điện đi kèm với kích thước Dài 33cm x Rộng 10cm x Sâu 12cm giúp bạn bài trí góc làm việc trở nên gọn gàng.\r\n\r\n', 4, 1, 'storage/images/75.webp', 0);
INSERT INTO `products` VALUES (76, 'Bàn Làm Việc Gỗ Có Kệ MOHO VLINE 602 Màu Nâu', 2169000.00, 'Các thiết kế trong bộ sưu tập nội thất VLINE mang trong mình nét đẹp đặc trưng của hồn Việt cùng vẻ đẹp năng động của thời đại. V” là viết tắt của từ “Việt” trong “Việt Nam”. “LINE” là các đường nét mang xu hướng hiện đại, phóng khoáng của ngày nay. \r\n\r\nBàn học, làm việc có kích thước vừa phải\r\nBàn làm việc kích thước vừa phải, gọn gàng, không chiếm quá nhiều diện tích. Mặt bàn có độ rộng vừa đủ có thể để được nhiều vật dụng như: tài liệu, máy vi tính, laptop, sách.', 4, 1, 'storage/images/76.webp', 0);
INSERT INTO `products` VALUES (77, 'Ghế Xoay Văn Phòng Ngả Lưng MOHO JEFE 701', 2190000.00, '* Ghế văn phòng có yếu tố công thái học (Ergonomic) - điều chỉnh phần tựa cổ, tay tựa, chiều cao ghế phù hợp mọi dáng người ngồi\r\nTựa đầu 3 chiều cỡ lớn\r\nTựa đầu 3 chiều giúp cho phần cổ và gáy của bạn được hỗ trợ linh hoạt ở bất kỳ tư thế nào khi sử dụng. Vải lưới cao cấp kế hợp cùng thiết kế bo cong không những mang lại sự chắc chắn, độ bền cao mà còn có thể điều chỉnh 3 chiều mang lại cảm giá thoải mái, phù hợp với thể trạng của rất nhiều người.\r\n\r\nHơn nữa, với sự linh hoạt của đầu ghế còn giúp bạn dễ dàng điều chỉnh độ cao trong khoảng 16 - 22cm và góc phù hợp với mọi loại bàn.', 4, 1, 'storage/images/77.webp', 0);
INSERT INTO `products` VALUES (78, 'Bàn Làm Việc Gỗ MOHO FYN 601 Màu Nâu', 2490000.00, 'Đề cao tính tiện nghi và tối giản trong không gian sống, MOHO mang đến thiết kế bàn làm việc FYN hiện đại, màu sắc trang nhã. Bàn làm việc FYN bổ sung cho không gian nhà bạn một sản phẩm đầy tính tiện nghi và thiết kế tinh tế, không chỉ giúp bạn đạt được hiệu suất làm việc tối đa mà còn mang đến cho góc làm việc của bạn thêm tính hài hòa về mặt thị giác.\r\n\r\nKiểu dáng bàn học, bàn làm việc thể hiện nét tinh tế, tối giản, hiện đại\r\nBàn làm việc FYN sở hữu thiết kế đơn giản, không cầu kỳ nhưng được xử lý tỉ mỉ từng chi tiết. Với các đường nét bo góc tại mặt bàn cùng với dán veneer tràm tự nhiên tại phần mặt hộc tủ, tạo nên cảm giác sang trọng và hiện đại cho góc làm việc, học tập của bạn.', 4, 1, 'storage/images/78.webp', 0);
