INSERT INTO `products` VALUES (80, 'Kệ Để Sách 3 Tầng MOHO WORKS 703', 1890000.00, 'Thiết kế đa năng, hiện đại\r\nKệ sách đa năng MOHO WORKS có thiết kế chia tầng mới lạ, hiện đại giúp bạn dễ dàng sử dụng làm kệ trang trí, kệ sách hoặc kệ bếp tùy sở thích.\r\nChiều cao giữa các tầng lớn\r\nKệ được thiết kế với khoảng cách gần 31.5cm giữa các tầng giúp bạn có thể trưng bày những vật cao hay thậm chí những cuốn sách, album lớn, thậm chí là trưng bày các thiết bị nhà bếp như lò vi sóng, máy xay sinh tố...\r\nĐiều chỉnh độ cao dễ dàng\r\nKệ có thể dễ dàng điều chỉnh độ cao lên đến 15mm bằng cách xoay nút điều chỉnh dưới chân kệ. Điều này giúp kệ cân bằng ngay cả khi đặt trên sàn nhà không bằng phẳng.', 4, 1, 'storage/images/80.webp', 0);
